package Array_1;public class Duplicate_In_Array_Bruteforce {    public static void main(String[] args) {        int[] arr = {1, 2, 3, 3, 4};        System.out.println(duplicate(arr));    }    static int duplicate(int[] arr) {        for (int i = 0; i < arr.length; i++) {            for (int j = i + 1; j < arr.length; j++) {                if (arr[i] == arr[j] && i != j) {                    return arr[j];                }            }        }        return -1;    }}