package Array_1;public class Find_Unique {    public static void main(String[] args) {        int[] arr = {1, 3, 1, 3, 6, 6, 7, 10, 7};        System.out.println(findUnique(arr));    }    public static int findUnique(int[] arr) {        int result = arr[0];        for (int i = 1; i < arr.length; i++) {            result = result ^ arr[i];        }        return result;    }}