package BinarySearch_Qus;// ceiling -> smallest number in array greater or = target.public class Ceiling {    public static void main(String[] args) {        int[] arr = {2, 3, 8, 9, 15, 16, 18};        int ans = ceilingno(arr, 18);        System.out.println(ans);    }    static int ceilingno(int[] arr, int target) {        //But what if the target is greater than the greatest number in the array        if (target > arr[arr.length - 1]) {            return -1;        }        int start = 0;        int end = arr.length;        while (start <= end) {            int mid = start + (end - start) / 2;// might be possible that (start + end) exceeds the range of int in java            if (target > arr[mid]) {                start = mid + 1;            } else if (target < arr[mid]) {                end = mid - 1;            } else {                return mid;            }        }        return start ;    }}