package BinarySearch_Qus; //floor -> greater number smaller or = target.public class Floor {     public static void main(String[] args) {         int[] arr = {2,3,8,14,16,17,18};         int ans = ceilingno(arr,15);         System.out.println(ans);     }     static int ceilingno(int[] arr,int target){         int start =0;         int end = arr.length;         while(start<=end){             int mid = start+(end-start)/2;// might be possible that (start + end) exceeds the range of int in java             if(target>arr[mid]){                 start = mid+1;             } else if (target<arr[mid]) {                 end = mid-1;             }             else{                 return mid;             }         }         return end;     } }