package Leetcode_Arr;//https://leetcode.com/problems/find-numbers-with-even-number-of-digits/description/public class FindNumberswithEvenNumberofDigits_1295 {    public static void main(String[] args) {        int[] nums = {12, 345, 2, 6, 78955};        System.out.println(findNumber(nums));    }    static int findNumber(int[] nums) {        int count = 0;        for (int num : nums) {            if (even(num)) {                count++;            }        }        return count;    }    //function to check weather a number contain even digit or not    static boolean even(int num) {        int numofdigit = digits2(num);//        if(numofdigit % 2==0){//            return true;//        }//        return false;        return numofdigit % 2 == 0;    }    // second way to count the digits in the number    static int digits2(int num) {        if (num < 0) {            num = num * -1;        }        return (int) (Math.log10(num)) + 1;    }    //count number of digits in the number//    static int digits(int num) {//        if (num < 0) {//            num = num * -1;//        }//        if (num == 0) {//            return 1;//        }//        int count = 0;//        while (num > 0) {//            count++;//            num /= 10;////        }    //  return count;    // }}